import './levelSelector.scss'
import {CardAuthor} from "../../components/cardAuthor/card.author";
import author from "../../mocks/authors.json";
import {TimelineMax, TweenMax} from "gsap";

class LevelSelectorPage extends Component {
    constructor(props: any) {
        super(props);
        this.initializeGame();
        console.log("EntrÃ³ al constructor...")
    }

    refLine = React.createRef();
    cards = () => {
        let cards: JSX.Element[] = [];
        for (let i = 0; i < 10; i++) {
            cards.push(<div className='level-section'></div>)
        }
        return cards;
    }


    initializeGame = () => {
        let b = document.body;

        let c = document.getElementById('character');
        let v = document.querySelector('#viewbox');
        let f = 24;
        let w;
        let h;
        if (c instanceof HTMLElement) {
            w = c.offsetWidth;
            h = c.offsetHeight;

            let a = 4;
            let s = 10;
            let b = 50;
            let r = 50;
            let p = Math.floor(c.offsetWidth / b);
            let q = a * p;
            let u = w - q;
            let m = new Array<Array<any>>();
            for (let h = 0; h < 10; h++) {
                m[h] = [];
                for (let y = 0; y < 10; y++) {
                    if (y == 0 || y == 9) {
                        m[h][y] = '*'
                    }
                    if (h == 0 || h == 9) {
                        m[h][y] = '*';
                    }
                }
            }
            m[1][1] = 'S';
            let z = new Game(c, v, m, a, s, b, r, u, w, h, f);

            z.initialize();
        }
    }

    list = () => {
        let list: JSX.Element[] = [];
        let quarters: JSX.Element[] = [];
        for (let i = 0; i < 10; i++) {
            quarters = [];
            for (let e = 0; e < 10; e++) {
                quarters.push(<div key={'s' + e + i} className="col" data-game-col={e}></div>);
            }
            list.push(<div key={'s' + i} className="row" data-game-row={i}>{quarters}</div>)
        }
        return list;
    }

    /*<div className='layoutContainer'>
            {cards()}
        </div>*/
    render() {
        return (<>
            <div className="container">
                <div className="screen" id="viewbox">
                    {this.list()}
                    <div className="character hide" id="character"></div>
                </div>
            </div>
        </>);
    }
}
