    }

    try {
      let o = m[i][j];
      let p = m[k][l];

      if (this.collidable(o) || this.collidable(p)) {
        return {
          x: j * w,
          y: i * h
        };
      } else if (o === 'X') {
        this.die();
      } else if (o === 'O') {
        this.gameWin();
      } else {
        return null;
      }
    } catch (e) {
      return null;
    }

    return 0;
  }

  moveCharacter(d, e) {
    let a = this.allowance;
    let w = this.width;
    let o = this.collision(d, e);

    if (o === null) {
      this.position.x += Math.floor(d);
    } else {
      if (e === 'l') {
        if (typeof o !== "number") {
          this.position.x = Math.floor(o.x) + w - a * 2;
        }
      } else if (e === 'r') {
        if (typeof o !== "number") {
          this.position.x = Math.floor(o.x) - w + a * 2;
        }
      }
    }
  }

  gravity() {
    let h = this.height;
    let s = this.space;
    let f = Math.floor(h * 0.2);
    let g = this.jumpSpeed >= 0 ? f : this.jumpSpeed;
    let p = this.collision(g, 'd') ? this.collision(g, 'd') : 0;

    if (p === null) {
      this.onLand = false;
      this.position.y += g;
    } else {
      if (typeof p !== "number") {
        this.position.y = p.y - h;
      }

      this.jumpSpeed = 0;
      this.onLand = true;
    }

    if (this.activeKeyY !== null && g < 0) {
      let o = this.collision(g, 'u');

      if (o === null) {
        this.jumpSpeed += f / 2;
      } else {
        this.jumpSpeed = 0;

        if (typeof o !== "number") {
          this.position.y = o.y + h - s * 2 + 2;
        }
      }
    }
  }

  renderCharacter() {
    let c = this.character;
    c.style.top = this.position.y + 'px';
    c.style.left = this.position.x + 'px';
  }

  animateCharacter(mode) {
    let c = this.character;

    if (mode === 'show') {
      c.classList.add('move');
      let a = this.activeKeyX;

      if (a === 37) {
        c.classList.add('flip');
      } else if (a === 39) {
        c.classList.remove('flip');
      }
    } else if (mode === 'hide') {
      c.classList.remove('move');
    }
  }

  createControls() {
    if (this.hasEvent === false) {
      this.hasEvent = true;
      let b = document.body;
      b.addEventListener('keydown', e => {
        if (e.keyCode === 38 && this.activeKeyY === null && this.onLand === true) {
          this.activeKeyY = 38;
          this.jumpSpeed = Math.floor(this.height * -0.7);
        } else if ((e.keyCode === 37 || e.keyCode === 39) && this.activeKeyX === null) {
          this.activeKeyX = e.keyCode;
          this.animateCharacter('show');
        }
      });
      b.addEventListener('keyup', e => {
        if (e.keyCode === 37 || e.keyCode === 39) {
          this.activeKeyX = null;
          this.animateCharacter('hide');
        } else if (e.keyCode === 38) {
          this.activeKeyY = null;
          this.jumpSpeed = 0;
        }
      });
    }
  }

  createMap() {
    let m = this.map;
    let v = this.viewbox;
    let s;

    if ("children" in v) {
      s = v.children;
    }

    let w = this.width;
    let h = this.height;

    for (let r of s) {
      let d = r.children;
      let i = +r.dataset.gameRow;

      for (let c of d) {
        let j = +c.dataset.gameCol;

        if (m[i][j] === '#') {
          c.classList.add('lawn');
        } else if (m[i][j] === 'H') {
          c.classList.add('ground');
        } else if (m[i][j] === 'O') {
          c.classList.add('finish');
        } else if (m[i][j] === '*' || m[i][j] === 'L') {
          c.classList.add('block');
        } else if (m[i][j] === ' ' || m[i][j] === 'X') {
          c.classList.add('space');
        } else if (m[i][j] === 'S') {
          this.position = {
            x: j * w,
            y: i * h
          };
        }
      }
    }
  }

  initializeCharacter() {
    let i = this.position.y;
    let j = this.position.x;
    let c = this.character;
    let u = this.charWidth;
    c.classList.remove('hide');
    this.renderCharacter();
  }

  render() {
    let m = this;
    let a = m.activeKeyX;
    let x = m.position.x;
    let y = m.position.y;
    setTimeout(() => {
      window.requestAnimationFrame(m.render.bind(m));
    }, 1000 / m.fps);

    if (a !== null) {
      let u = m.charWidth;
      let c = m.character;

      if (this.won === false && this.dead === false) {
        if (a === 37) {
          m.moveCharacter(Math.floor(u * -0.15), 'l');
        } else if (a === 39) {
          m.moveCharacter(Math.floor(u * 0.15), 'r');
        }
      }
    }

    m.gravity();
    m.viewbox.style.top = y > m.height * 9 ? m.height * -5 + 'px' : '0';
    m.renderCharacter();
  }

  initialize() {
    this.won = false;
    this.dead = false;
    this.viewbox.classList.add('active');

    if (this.hasEvent === false) {
      this.render();
    }

    this.createControls();
    this.createMap();
    this.initializeCharacter();
  }

}

